import React, { useEffect, useState } from 'react';
import Modal from 'react-bootstrap/Modal';


function Header() {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);

    const handleShow = () => {
        setShow(true);

    };

    useEffect(() => {
        document.title = ` ${show} tut`;
    }, [show]);

    return (
        <>

            <Modal show={show} onHide={handleClose} className="Menu-modul" >
                <div className="container">
                    <Modal.Body className="p-5">
                        <h4><a>Clients</a></h4>
                        <h4><a>About</a></h4>
                        <h4><a>Work</a></h4>
                        <h6 className="mt-5">Letâ€™s talk.</h6>
                        <h6><a>biuro@craftmediahouse.pl</a></h6>
                    </Modal.Body>
                </div>
            </Modal>

            <div className="containerHamburger w-100">
                <div className="container menu-header-container text-end">
                    <div onClick={(show ? handleClose : handleShow)} className={"hamburglar " + (show ? 'is-open' : 'is-closed')}>
                        <div className="burger-icon">
                            <div className="burger-container">
                                <span className="burger-bun-top"></span>
                                <span className="burger-filling"></span>
                                <span className="burger-bun-bot"></span>
                            </div>
                        </div>
                        <div className="burger-ring">
                            <svg className="svg-ring">
                                <path className="path" fill="none" stroke="#242424" strokeMiterlimit="10" strokeWidth="4"
                                    d="M 34 2 C 16.3 2 2 16.3 2 34 s 14.3 32 32 32 s 32 -14.3 32 -32 S 51.7 2 34 2" />
                            </svg>
                        </div>

                        <svg width="0" height="0">
                            <mask id="mask">
                                <path xmlns="http://www.w3.org/2000/svg" fill="none" stroke="#242424" strokeMiterlimit="10"
                                    strokeWidth="4" d="M 34 2 c 11.6 0 21.8 6.2 27.4 15.5 c 2.9 4.8 5 16.5 -9.4 16.5 h -4" />
                            </mask>
                        </svg>
                        <div className="path-burger">
                            <div className="animate-path">
                                <div className="path-rotation"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Header;
