import React, { useEffect, useState } from 'react';

import Modal from 'react-bootstrap/Modal';


function Header() {
    const [show, setShow] = useState(false);
    const handleClose = () => { setShow(false); document.body.classList.remove('removeBodyModalStyles'); };
    const handleShow = () => { setShow(true); document.body.classList.add('removeBodyModalStyles') };

    useEffect(() => {
        document.title = ` ${show} tut`;
    }, [show]);

    return (
        <>

            <Modal show={show} onHide={handleClose} className={"Menu-modul " + (show ? 'removeBodyModalStyles' : '')} >
                <Modal.Body className="p-5">
                    <h4><a>Clients</a></h4>
                    <h4><a>About</a></h4>
                    <h4><a>Work</a></h4>
                    <h6 className="mt-5">Letâ€™s talk.</h6>
                    <h6><a>biuro@craftmediahouse.pl</a></h6>
                </Modal.Body>
            </Modal>

        </>
    );
}

export default Header;
